`;
const ServicesContainer = styled.div`
  
//background-image: url(https://www.citysidedrivingschool.co.uk/wp-content/uploads/2014/12/banner-1.jpg);

background-Size: 100%;
    width: 80%;
    height: 400px;
    background: #999;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: bottom;
    margin: 5%;
    border-radius: 5px;
 
`;




const About = () => {
 
 
 
  return (
    <div
